```js
 function initMap() {  
    var map = new google.maps.Map(document.getElementById("map-canvas"), {  
        center: new google.maps.LatLng(40.731, -73.997), 
        //如果定位失败后，地图默认的中心位置 
        zoom: 3, //地图比例，越大越详细  
        fullscreenControl: false,  //关闭全屏按钮
        zoomControl: true,  //开启放大缩小比例按钮  
        streetViewControl: false,  //关闭街景模式  
        mapTypeId: google.maps.MapTypeId.ROADMAP  
    });  
    // 建立可以拖拽选择位置的小红点   
    var marker = new google.maps.Marker({  
        position: map.getCenter(),  
        draggable: true,  
        map: map  
    });  
    // 小红点的位置，设置可移动，添加事件获取小红点当前的经纬度    
    google.maps.event.addListener(map, "idle", function () {  
        marker.setPosition(map.getCenter());  
        document.getElementById("LatLng").value = map.getCenter().lat().toFixed(6) + "," + map.getCenter().lng().toFixed(6);  
    });  
    google.maps.event.addListener(marker, "dragend", function (mapEvent) {  
        map.panTo(mapEvent.latLng);  
    });  
    // initialize geolocation  
    //开启自动定位 
    if (navigator.geolocation) {  
        navigator.geolocation.getCurrentPosition(function (position) {  
          map.setCenter(new google.maps.LatLng(position.coords.latitude, position.coords.longitude));  
        }, function () {  
          document.getElementById('now').innerHTML =  
            "Sorry, geolocation API failed to detect your location.Please drag the mark to select your location.";  
        });  
      }  
    //geocoder是解析经纬度的，用来输出小红点的位置。  
    //geocoder2是用来解析输入框中输入的位置，用来挪动小红点的位置。
    //其实感觉一个就可以，但是文档里说location和address一次只能一个。
    var geocoder = new google.maps.Geocoder;  
    var geocoder2= new google.maps.Geocoder;  
    var infowindow = new google.maps.InfoWindow;  
    document.getElementById('select').addEventListener('click', function () {  
        geocodeLatLng(geocoder, map, infowindow);  
    });  
    document.getElementById('search_btn').addEventListener('click',function(){  
        gecodeAddress(geocoder2,map);  
    })  
}  
// initialize geocoder  
//根据小红点的经纬度来解析地理位置  
function geocodeLatLng(geocoder, map, infowindow) {  
    var input = document.getElementById('LatLng').value;  
    var latlngStr = input.split(',', 2);  
    var latlng = {  
        lat: parseFloat(latlngStr[0]),  
        lng: parseFloat(latlngStr[1])  
    };  
    geocoder.geocode({  
        'location': latlng  
    },   
    function (results, status) {  
        if (status === 'OK') {  
            if (results[0]) {  
                map.setZoom(4);  
                //得到的数据格式可以参考：https://maps.google.com/maps/api/geocode/json?latlng=1.3437449,103.7540048&key=AIzaSyC1wF4wAiScz7nIbn9Kek698GRKFzyNeY8  
               //根据需要获取地点，此处获取的是国家。 
                var length = results[0].address_components.length;  
                for (var j = 0; j < length; j++) {  
                    if ((results[0].address_components[j].types[0] === 'country') &&(results[0].address_components[j].types[1] === 'political')) {  
                        var city = results[0].address_components[j].long_name;  
                        document.getElementById('now').innerHTML = "Your location:" + city;  
                    }  
                }  
            } else {  
                window.alert('Please try again.');  
            }  
        } else {  
            window.alert('Geocoder failed due to: ' + status);  
        }  
    });  
}  
//获取搜索框输入内容，解析地理位置  
function gecodeAddress(geocoder2,map){  
    let address=document.getElementById('search_ipt').value;  
    geocoder2.geocode({  
        'address':address  
    },  
    function(results,status){  
        if (status === 'OK') {  
            if (results[0]) {  
                map.setZoom(4);  
                //挪动地图中心
                map.setCenter(results[0].geometry.location);  
                var length = results[0].address_components.length;  
                //获取位置，同经纬度    
                for (var j = 0; j < length; j++) {  
                    if ((results[0].address_components[j].types[0] === 'country') &&(results[0].address_components[j].types[1] === 'political')) {  
                        var city = results[0].address_components[j].long_name;  
                        document.getElementById('now').innerHTML = "Your location:" + city;  
                    }  
                }  
            } else {  
                window.alert('Please try again.');  
            }  
        } else {  
            window.alert('Geocoder failed due to: ' + status);  
        }  
    })  
} 

